def is_leap_year(year: int) -> bool:
    # A year is a leap year if it's divisible by 4
    if year % 4 == 0:
        # Except if it's also divisible by 100 and not by 400
        if year % 100 == 0:
            return year % 400 == 0
        else:
            return True
    else:
        return False
