def totient(n: int) -> int:
    result = 0
    for i in range(1, n+1):
        is_rel_prime = True
        for j in range(2, int(i**0.5) + 1):
            if i % j == 0:
                is_rel_prime = False
                break
        if is_rel_prime:
            result += 1
    return result
