import math
def is_square(x: int) -> bool:
    return math.sqrt(x).is_integer()

def is_fibonacci_number(n: int) -> bool:
    # A number x is Fibonacci if and only if one or both of (5*x^2 + 4) or (5*x^2 - 4)
    # are perfect squares
    return is_square(5 * n * n + 4) or is_square(5 * n * n - 4)
