def are_anagrams(s1: str, s2: str) -> bool:
    # Remove non-alphanumeric characters and convert to lowercase
    s1 = ''.join(char for char in s1.lower() if char in string.ascii_letters + string.digits)
    s2 = ''.join(char for char in s2.lower() if char in string.ascii_letters + string.digits)

    # Check the lengths of the strings are equal, because anagrams must have the same number of characters.
    if len(s1) != len(s2):
        return False

    # Sort the characters in each string and compare them
    return sorted(s1) == sorted(s2)
