import math
def is_prime(x: int) -> bool:
    if x < 2:
        return False
    for i in range(2, math.isqrt(x) + 1):
        if x % i == 0:
            return False
    return True
def euler_totient(n: int) -> int:
    tot = n
    p = 2
    while p * p <= n:
        if n % p == 0:
            while n % p == 0:
                n //= p
            tot -= tot // p
        p += 1
    if n > 1:
        tot -= 1
    return tot
