def count_fizz_buzz(n: int) -> int:
    fizz_count = 0
    buzz_count = 0
    for i in range(1, n+1):
        if i % 3 == 0 and i % 5 != 0:
            fizz_count += 1
        elif i % 5 == 0:
            buzz_count += 1
        elif i % 3 == 0 and i % 5 == 0:
            fizz_count += 1
            buzz_count += 1
    return fizz_count + buzz_count
